def test_prefix_cache_comparison():
    """对比启用和禁用前缀缓存的性能差异"""
    
    print("\n" + "=" * 60)
    print("对比测试: 启用 vs 禁用前缀缓存")
    print("=" * 60)
    
    # 测试数据：相同的系统提示和多轮对话
    system_prompt = "你是一个有帮助的AI助手。"
    user_inputs = [
        "你是谁?",
        "你能做什么?",
        "给我讲个笑话。",
    ]
    
    for enable_cache in [False, True]:
        cache_status = "启用" if enable_cache else "禁用"
        print(f"\n--- 前缀缓存: {cache_status} ---")
        
    
        llm = LLM(
            model="/workspace/pangyunhe/models/custom/qwen3-8b-lora",  # 使用你要测试的模型
            enable_prefix_caching=True,
            gpu_memory_utilization=0.9,
            tensor_parallel_size=1,
        )
        
        # 设置采样参数
        sampling_params = SamplingParams(
            max_tokens=1,
            logprobs=5,  # 返回前5个最可能token的logprobs
            temperature=0.7,
            top_p=0.95,
        )
        
        
        total_time = 0
        dialogue_history = []
        
        for idx, user_input in enumerate(user_inputs, 1):
            dialogue_history.append(f"用户: {user_input}")
            full_prompt = system_prompt + "\n" + "\n".join(dialogue_history) + "\n助手:"
            
            start_time = time.time()
            outputs = llm.generate([full_prompt], sampling_params)
            elapsed_time = time.time() - start_time
            
            response = outputs[0].outputs[0].text.strip()
            dialogue_history.append(f"助手: {response}")
            
            total_time += elapsed_time
            print(f"第 {idx} 轮: {elapsed_time:.3f} 秒")
        
        print(f"总耗时: {total_time:.3f} 秒")


